package Miscellanous;

public class TraingleWithSpace {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=6;i>0;i--) {
			for(int s=i;s>6;s++) {
				System.out.print(" ");
			}
			for(int j=1;j<=i;j++) {
			System.out.print("*");
		   }
			System.out.println(" ");
		 }

	}

}
